      out.write("function registerUser() {\r\n");
      out.write("    var userName = $(\"#userName\").val();\r\n");
      out.write("    var nickName = $(\"#nickName\").val();\r\n");
      out.write("    var loginName = $(\"#loginNameReg\").val();\r\n");
      out.write("    var loginPwdReg = $(\"#loginPwdReg\").val();\r\n");
      out.write("    var loginPwdRegRepeat = $(\"#loginPwdRegRepeat\").val();\r\n");
      out.write("    var userImage = $(\"#userImage\").val();\r\n");
      out.write("    if (loginPwdReg != loginPwdRegRepeat) {\r\n");
      out.write("        alert(\"两次输入的密码不一致，请重新输入！\");\r\n");
      out.write("        return ;\r\n");
      out.write("    }\r\n");
      out.write("    var reqUrl = \"index.php?f=c_user&c=CUser&m=registerUserAjax\";\r\n");
      out.write("    var json = new Object();\r\n");
      out.write("    json.userName = userName;\r\n");
      out.write("    json.nickName = nickName;\r\n");
      out.write("    json.loginName = loginName;\r\n");
      out.write("    json.loginPwd = loginPwdReg;\r\n");
      out.write("    json.userImage = userImage;\r\n");
      out.write("    $.ajax({\r\n");
      out.write("        url: reqUrl,\r\n");
      out.write("        type: 'POST',\r\n");
      out.write("        data: {\r\n");
      out.write("            jsonReq: JSON.stringify(json)\r\n");
      out.write("        },\r\n");
      out.write("        dataType: 'json',\r\n");
      out.write("        success: onRegisterUserOk,\r\n");
      out.write("        error: onRegisterUserError\r\n");
      out.write("    });\r\n");
      out.write("    return false;\r\n");
      out.write("}\r\n");
      out.write("function onRegisterUserOk(json) {\r\n");
      out.write("    //alert(JSON.stringify(json));\r\n");
      out.write("    var rst = confirm(\"您是否希望我们记住您？（这样您在两周内就可以不用登录直接访问！）\");\r\n");
      out.write("    var rememberMode = 0;\r\n");
      out.write("    if (rst) {\r\n");
