      out.write("    alert(\"注册用户失败：\" + msg.responseText + \"！\");\r\n");
      out.write("}\r\n");
      out.write("\r\n");
      out.write("\r\n");
      out.write("function setCookieAjax(userId, rememberMode) {\r\n");
      out.write("    var reqUrl = \"ajax?cmd=setCookieAjax&userId=\" + userId + \"&rememberMode=\" + rememberMode;\r\n");
      out.write("    $.ajax({\r\n");
      out.write("        url: reqUrl,\r\n");
      out.write("        type: 'GET',\r\n");
      out.write("        dataType: 'json',\r\n");
      out.write("        success: onSetCookieAjaxOk,\r\n");
      out.write("        error: onSetCookieAjaxError\r\n");
      out.write("    });\r\n");
      out.write("}\r\n");
      out.write("function onSetCookieAjaxOk(json) {\r\n");
      out.write("    window.location.href = g_originUrl;\r\n");
      out.write("}\r\n");
      out.write("function onSetCookieAjaxError(msg) {\r\n");
      out.write("    alert(\"设置Cookie失败：\" + msg.responseText + \"！\");\r\n");
      out.write("    window.location.href = g_originUrl;\r\n");
      out.write("}\r\n");
      out.write("\r\n");
      out.write("function loginUser() {\r\n");
      out.write("    var loginName = $(\"#loginName\").val();\r\n");
      out.write("    var loginPwd = $(\"#loginPwd\").val();\r\n");
      out.write("    if (\"\" == loginName || \"\" == loginPwd) {\r\n");
      out.write("        alert(\"登录名、密码不能为空，请重新输入！\");\r\n");
      out.write("        return ;\r\n");
      out.write("    }\r\n");
      out.write("    var reqUrl = \"ajax?cmd=loginUserAjax&loginName=\" + loginName + \"&loginPwd=\" + loginPwd;\r\n");
      out.write("    $.ajax({\r\n");
      out.write("        url: reqUrl,\r\n");
      out.write("        type: 'GET',\r\n");
      out.write("        dataType: 'json',\r\n");
      out.write("        success: onLoginUserAjaxOk,\r\n");
      out.write("        error: onLoginUserAjaxError\r\n");
      out.write("    });\r\n");
      out.write("    return false;\r\n");
      out.write("}\r\n");
      out.write("function onLoginUserAjaxOk(json) {\r\n");
      out.write("    var userId = json.userId;\r\n");
      out.write("    var rememberMe = 0;\r\n");
      out.write("    if ($(\"#rememberMe\").prop(\"checked\")) {\r\n");
      out.write("        rememberMe = 1;\r\n");
      out.write("    }\r\n");
      out.write("    if (userId <= 0) {\r\n");
      out.write("        alert(\"我们无法找到您提供的用户名密码所对应的用户，请联系系统管理员！\");\r\n");
      out.write("        return;\r\n");
      out.write("    }\r\n");
      out.write("    setCookieAjax(userId, rememberMe);\r\n");
      out.write("}\r\n");
